CREATE DEFINER=`usrsiakad`@`%` FUNCTION `gethari` () RETURNS INT(11) NO SQL
BEGIN
DECLARE id_hari INT;
select
case
when date_format(now(),'%w') = 0 THEN 7
when date_format(now(),'%w') = 1 THEN 1
when date_format(now(),'%w') = 2 THEN 2
when date_format(now(),'%w') = 3 THEN 3
when date_format(now(),'%w') = 4 THEN 4
when date_format(now(),'%w') = 5 THEN 5
when date_format(now(),'%w') = 6 THEN 6
end as hari
INTO id_hari;

RETURN id_hari;
END$$

CREATE DEFINER=`root`@`localhost` FUNCTION `getmax_nilai` (`kd_kontrak_kuliah` VARCHAR(255)) RETURNS INT(11) NO SQL
BEGIN
DECLARE id_shift INT;

SELECT MAX(jml_nim) as max_nim 
INTO id_shift
FROM v_jumlah_nim where kd_kontrak_kuliah=kd_kontrak_kuliah;

RETURN id_shift;
END$$

CREATE DEFINER=`usrsiakad`@`%` FUNCTION `getshift` () RETURNS INT(11) NO SQL
BEGIN
DECLARE id_shift INT;

SELECT id_shift_perkuliahan 
INTO id_shift
FROM t_shift_perkuliahan where ((time(now()) >= waktu_mulai) and (time(now()) <= waktu_selesai));

RETURN id_shift;
END$$

